Action.ExecScriptExpression:
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using Triggernometry;
using static Triggernometry.Action;
using static Triggernometry.RealPlugin;
using static Triggernometry.Interpreter.StaticHelpers;
using Action = Triggernometry.Action;

plug.UnregisterNamedCallback("LatihasCreate");
plug.RegisterNamedCallback("LatihasCreate", new Action<object, string>(LatihasCreate.Callback), null);

public class LatihasCreate {
 private static readonly Trigger _tri = new();
 private static readonly List<string> _Abilities = new() { "掌握", "崇敬", "长期俭约", "改革", "阔步", "俭约" };

 public static void Callback(object _, string str) {
  var proDic = new Dictionary<string, List<string>>();
  foreach (var pair in GetDictVariable(false, "LatihasCreatorDic").KeyValuePairs) {
   var spl = pair.Value.ToString().Split(',').ToList();
   foreach (var n in pair.Key.Split('|')) proDic[n] = spl;
  }
  foreach (var pair in new Dictionary<string, List<string>> {
     //老主顾
     { "近东风格的收藏品|寒冷地带的特产|黑涡团军需品|别具匠心的礼物|孤儿院的日用品", new List<string> { "工匠的神速技巧", "坯料制作" } },
     { "基拉巴尼亚的特产|东方风格的收藏品|金碟游乐场的纪念品|梅氏一族的日用品|解放军的军需品", new List<string> { "工匠的神速技巧", "坯料制作" } },
     { "基拉巴尼亚医药品|防鲨用品|库尔札斯防寒用品|翠水乡特产|云海珍宝", new List<string> { "工匠的神速技巧", "坯料制作" } },
     { "伊修加德菜肴必需品|崖畔亭蒸馏酒必需品|崖畔亭包子必需品|崖畔亭的备用品|酿造奶酒的必需品", new List<string> { "工匠的神速技巧", "坯料制作" } },
     { "传单制作必需品|新菜肴必需品|舞台服装必需品|飞空艇改装必需品|蜂箱夜总会特产", new List<string> { "工匠的神速技巧", "坯料制作" } },
     { "龙族用采集工具部件|龙族用缝纫工具|龙族用工匠工具部件|龙族用招待菜肴必需品|印刷必需品", new List<string> { "工匠的神速技巧", "坯料制作" } },
     { "纪念医院的日用品|纪念医院的医疗用品|纪念医院的康复训练用品|纪念医院的茶会用品|纪念医院的绘画道具", new List<string> { "工匠的神速技巧", "坯料制作" } },
     { "留学生用文具|留学生用魔器|魔法餐盒|特训用魔法人偶|辅助学习用品", new List<string> { "工匠的神速技巧", "坯料制作" } },
     { "牧羊工具|山岳防寒用品|孚布特风土美食|孚布特样式工艺品|可爱的礼物", new List<string> { "工匠的神速技巧", "坯料制作" } },
     { "缓解腰痛的必需品|魅惑猫咪的必需品|魅力十足的立体模型|应对眼疲劳的必需品|研究员的休息必需品", new List<string> { "工匠的神速技巧", "坯料制作" } },
    })
  foreach (var n in pair.Key.Split('|'))
   proDic[n] = pair.Value;

  Log(DebugLevelEnum.Info, $"Creating: {str}");
  if (!proDic.Keys.Contains(str)) return;
  var list = proDic[str];
  var timeCount = 1000;
  for (var i = 0; i < list.Count; i++) {
   var l = list[i];
   var finalCount1 = timeCount;
   Task.Run(async delegate {
    await Task.Delay(finalCount1);
    plug.QueueAction(fakectx, _tri, null,
     new Action {
      ActionType = ActionTypeEnum.NamedCallback.ToString(),
      NamedCallbackName = "command",
      NamedCallbackParam = $"/ac {l}",
     }, DateTime.Now, true);
   });
   timeCount += _Abilities.Contains(l) ? 1750 : 2250;
   if (i != list.Count - 1) continue;
   var finalCount = timeCount;
   Task.Run(async delegate {
    await Task.Delay(finalCount);
    plug.QueueAction(fakectx, _tri, null,
     new Action {
      ActionType = ActionTypeEnum.LogMessage.ToString(),
      LogMessageText = $"[Latihas Create TTS] 制作完成：{str}",
      LogProcess = "true"
     }, DateTime.Now, true);
   });
  }
 }
}
